"use strict";

for (var tabNavs = document.querySelectorAll(".nav-tab"), tabPanes = document.querySelectorAll(".tab-pane"), i = 0; i < tabNavs.length; i++) {
  tabNavs[i].addEventListener("click", function (a) {
    a.preventDefault();

    for (var t = a.target.getAttribute("data-tab"), e = 0; e < tabNavs.length; e++) {
      t === tabPanes[e].getAttribute("data-tab-content") ? (tabNavs[e].classList.add("active"), tabPanes[e].classList.add("active")) : (tabNavs[e].classList.remove("active"), tabPanes[e].classList.remove("active"));
    }
  });
}

function myFunction() {
  var a = document.getElementById("myTopnav");
  "topnav" === a.className ? a.className += " responsive" : a.className = "topnav";
}